import React, { Component } from 'react';
import styles from './styles';
import { withStyles} from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import { Gauge } from '@ant-design/charts';

class Gauge1 extends Component {

      render() {
        const {data}=this.props;
        const config = {
            title: {
              visible: false,
              text: 'viet nguyen',
            },
             
            value: data,
            min: 0,
            max: 100,
            range: [0, 100],
            color: ['l(0) 0:#0000ff 1:#00FF00'],
            axis: {
              offset: -5,
              tickLine: {
                visible: true,
                length: 10,
              },
              label: { visible: false },
            },
            pivot: {
              visible: true,
              thickness: 10,
              pointer: {
                visible: true,
                style: { fill: '#e22354' },
              },
              pin: {
                visible: true,
                style: { fill: '#e8e6ea' },
              },
            },
            statistic: {
              visible: true,
              position: ['50%', '100%'],
              text: `${data} %`,
              color: '#2e3033',
              size: 20,
            },
          };
          return ( 
                      <Gauge 
                      {...config}
                      style={{maxHeight:'10%'}}


                      /> 
          );
    }
}
Gauge1.propTypes={
    classes:PropTypes.object,
}   
export default withStyles(styles)(Gauge1);
