        textAlign: 'center',
        width: '100%',
        margin:'0px',
        height: '95vh',
        flex:'1 0 auto',
    },
    signup:{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
        flex:'1 0 auto',
    },
    card:{
        maxWidth: '80%',
        maxHeight: '40%',
    },
    typography:{
        fontSize: '1em',
    },
    textField:{
        fontSize: '60%',
    },
    button:{
        margin: 10,
        width: '40%',
        fontSize: '70%',
        
    }
});
export default styles;
