import  AxiosService from '../services/axiosService';
import qs from 'query-string';

// http://api.openweathermap.org/data/2.5/weather?q=Hanoi&appid=5b7aced2e33ce6c8cf6a0e7cb1b1aa69&units=metric METHOD : POST
const API_ENDPOINT = "http://api.openweathermap.org"
export const getListWeather =(url,params={})=>{
    let queryParams='';
    if(Object.keys(params).length >0){
        queryParams=`?${qs.stringify(params)}`;
    }
    return AxiosService.get(`${API_ENDPOINT}/${url}${queryParams}`)
}
