      time: '2020:11:25 19:15:45',
    }
  ],
};

const reducer = (state = inittialState, action) => {
  switch (action.type) {
    case types.FETCH_ALARM: {
      return {
        ...state,
      };
    }
    case types.FETCH_ALARM_SUCCESS: {
      const { data } = action.payload;
      return {
