import React, { Component } from 'react';
import { withStyles, Button } from '@material-ui/core';
import PropTypes from 'prop-types';
import { compose, bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { Redirect } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import renderTextField from '../../components/FormHelper/TextField/index';
import * as alarmActions from './../../actions/alarm';
import Table from './../../components/Table';
import styles from './styles';

class ConfigSystem extends Component {
  componentDidMount() {
    // const { alarmActionCreators }=this.props;
    // const { fetchListAlarm } = alarmActionCreators;
    // fetchListAlarm();
  }

  handleSubmitForm01 = data => {
    //const {devicename,formdate,todate,type}= data;
    console.log(data);
    // const { alarmActionCreators }=this.props;
    // const { filterAlarm } = alarmActionCreators;
    // filterAlarm(data);
  };
  handleSubmitForm02 = data => {
    //const {devicename,formdate,todate,type}= data;
    console.log(data);
    // const { alarmActionCreators }=this.props;
    // const { filterAlarm } = alarmActionCreators;
    // filterAlarm(data);
  };
  render() {
    const { handleSubmit, redirectToReferrer } = this.props;

    const rows = [
      {
        id: 1,
        name: 'việt nguyễn',
        email: 'vietnguyen940@gmail.com',
        time: '2020:11:25 19:15:45',
      },
      {
        id: 2,
        name: 'việt nguyễn',
        email: 'vietnguyen940@gmail.com',
        time: '2020:11:25 19:15:45',
      },
      {
        id: 3,
        name: 'việt nguyễn',
        email: 'vietnguyen940@gmail.com',
        time: '2020:11:25 19:15:45',
      },
      {
        id: 4,
        name: 'việt nguyễn',
        email: 'vietnguyen940@gmail.com',
        time: '2020:11:25 19:15:45',
      },
    ];
    const columns = [
      {
        id: 'id',
        label: 'id',
        minWidth: 10,
        align: 'center',
      },
      {
        id: 'name',
        label: 'Tên',
        minWidth: 100,
        align: 'center',
      },
      {
        id: 'email',
        label: 'Email',
        minWidth: 170,
        align: 'center',
      },
      {
        id: 'time',
        label: 'Thời gian',
        minWidth: 170,
        align: 'center',
      },
    ];
    if (
      redirectToReferrer === false &&
      localStorage.getItem('token:') === null
    ) {
      return <Redirect to={'/'} />;
    }

    return (
      <div className="p-1 pt-5" style={{}}>
        <Grid container spacing={1}>
          <Grid item xs={12} md={12} style={{ textAlign: 'center' }}>
            <label
              className="font-weight-bolder h6 w-100 mb-1 p-1"
              style={{
                height: 'fit-content',
                maxHeight: '40vh',
                borderBottom: '2px solid #00CC00',
              }}
            >
              Cài đặt chung
            </label>
          </Grid>
          <Grid item xs={12} md={12}>
            <form onSubmit={handleSubmit(this.handleSubmitForm01)}>
              <Grid container spacing={1} className=" p-4 pt-1">
                <Grid item xs={12} md={12} className="textAlign: center">
                  <label
                    className="font-weight-bolder h6 w-100 mb-5 p-1"
                    style={{
                      height: 'fit-content',
                      maxHeight: '40vh',
                      borderBottom: '2px solid #00CC00',
                    }}
                  >
                    Cài đặt giá điện:
                  </label>
                </Grid>
                <Grid item sx={12} md={3}>
                  <Field
                    id="price1"
                    label="Giá điện mức 50kWh"
                    name="price_1"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 15 } }} // font size of input label
                    type="text"
                    size="small"
                    component={renderTextField}
                  />
                </Grid>
                <Grid item sx={12} md={3}>
                  <Field
                    id="price2"
                    label="Giá điện mức 100kWh"
                    name="price_2"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 15 } }} // font size of input label
                    type="text"
                    size="small"
                    component={renderTextField}
                    value={1000}
                  />
                </Grid>
                <Grid item sx={12} md={3}>
                  <Field
                    id="price3"
                    label="Giá điện mức 200kWh"
                    name="price_3"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 15 } }} // font size of input label
                    type="text"
                    size="small"
                    component={renderTextField}
                  />
                </Grid>
                <Grid item sx={12} md={3}>
                  <Field
                    id="pricesolar"
                    label="Giá điện mặt trời"
                    name="pricesolar"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 15 } }} // font size of input label
                    type="text"
                    size="small"
                    component={renderTextField}
                  />
                </Grid>
              </Grid>
              
              <Grid item xs={12} md={6} className="p-4">
                <Button
                  className="mt-3 mr-2"
                  color="primary"
                  style={{ backgroundColor: '#00CC33' }}
                  variant="contained"
                  type="submit"
                  size="small"
                >
                  Đồng ý
                </Button>
                <Button
                  className="mt-3"
                  color="secondary"
                  style={{ backgroundColor: '#FF0000' }}
                  variant="contained"
                  type="submit"
                  size="small"
                >
                  Hủy bỏ
                </Button>
              </Grid>
            </form>
            <Grid item xs={12} md ={12} className ="p-1">
                <Table rows={rows} columns={columns} />
              </Grid>
          </Grid>
          <Grid item xs={12} md={6}>
            <form onSubmit={handleSubmit(this.handleSubmitForm02)}>
              <Grid container spacing={1} className=" p-4">
                <Grid item xs={12} md={12} className="textAlign: center">
                  <label
                    className="font-weight-bolder h6 w-100 mb-5 p-1"
                    style={{
                      height: 'fit-content',
                      maxHeight: '40vh',
                      borderBottom: '2px solid #00CC00',
                    }}
                  >
                    Cài đặt email cảnh báo:
                  </label>
                </Grid>
                <Grid item sx={12} md={6}>
                  <Field
                    id="name"
                    label="name"
                    name="name"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    type="text"
                    InputLabelProps={{
                      style: { fontSize: 15 },
                    }}
                    size="small"
                    component={renderTextField}
                  />
                </Grid>
                <Grid item sx={12} md={6}>
                  <Field
                    id="email"
                    label="Email"
                    name="email"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 15 } }} // font size of input label
                    type="email"
                    size="small"
                    component={renderTextField}
                  />
                </Grid>
                
              </Grid>
              <Grid item xs={12} md={6} className="p-4">
                <Button
                  className="mt-3 mr-2"
                  color="primary"
                  variant="contained"
                  style={{ backgroundColor: '#00CC33' }}
                  type="submit"
                  size="small"
                >
                  Đồng ý
                </Button>
                <Button
                  className="mt-3"
                  color="secondary"
                  variant="contained"
                  style={{ backgroundColor: '#FF0000' }}
                  type="submit"
                  size="small"
                >
                  Hủy bỏ
                </Button>
              </Grid>
            </form>
          </Grid>
          <Grid item xs={12} md={6} className="textAlign:center  p-4">
            <label
              className="font-weight-bolder h6 w-100 mb-5 p-1"
              style={{
                height: 'fit-content',
                maxHeight: '40vh',
                borderBottom: '2px solid #00CC00',
              }}
            >
              Danh sách tài khoản email
            </label>
            <Grid
              container
              spacing={1}
              className="mr-3 p-1 mb-0 max-h-50"
              style={{
                backgroundColor: 'rgba( 0, 0, 0, 0.2)',
                border: '2px solid #00CC33',
              }}
            >
              <Table rows={rows} columns={columns} />
            </Grid>
          </Grid>
        </Grid>
      </div>
    );
  }
}

ConfigSystem.propTypes = {
  classes: PropTypes.object,
  handleSubmit: PropTypes.func,
  invalid: PropTypes.bool,
  submitting: PropTypes.bool,
  redirectToReferrer: PropTypes.bool,
  listAlarm: PropTypes.array,
  alarmActionCreators: PropTypes.shape({
    fetchListAlarm: PropTypes.func,
  }),
};

const FORM_ALARM = 'TASK_ALARM';
const withReduxForm = reduxForm({
  form: FORM_ALARM,
});
const mapStateToProps = state => {
  return {
    listAlarm: state.alarm.listAlarm,
    redirectToReferrer: state.auth.redirectToReferrer,
  };
};
const mapDispatchToProps = (dispatch, props) => {
  return {
    alarmActionCreators: bindActionCreators(alarmActions, dispatch),
  };
};
const withConnect = connect(mapStateToProps, mapDispatchToProps);
export default compose(
  withStyles(styles),
  withConnect,
  withReduxForm,
)(ConfigSystem);
