        justifyContent: 'center',
        textAlign: 'center',
        flex:'1 0 auto',
    },
    cardContent:{
        fontSize: '1em',
    },
    card:{
        maxWidth: '100%',
    },
    typography:{
        fontSize: '1.3em',
    },
    textField:{
        fontSize: '60%',
    },
