import React, { Component } from 'react';
import { withStyles, Button } from '@material-ui/core';
import PropTypes from 'prop-types';
import { compose, bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { Redirect } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import renderTextField from '../../components/FormHelper/TextField/index';
import * as alarmActions from './../../actions/alarm';
import * as priceActions from './../../actions/prices';
import Table from './../../components/Table';
import styles from './styles';

// function format money
function formatCash(value) {
  value = value.toString();
  return value
    .split('')
    .reverse()
    .reduce((prev, next, index) => {
      return (index % 3 ? next : next + ',') + prev;
    });
}

class ConfigSystem extends Component {
  componentDidMount() {
    const { alarmActionCreators,priceActionCreators }=this.props;
    const { refeshprice} = priceActionCreators;
    refeshprice();
    const { refeshListEmailAlarm } = alarmActionCreators;
    refeshListEmailAlarm();
  }

  handleSubmitForm01 = data => {
    const { levelprice01, levelprice02, levelprice03, solarprice } = data;
    const {priceActionCreators} = this.props;
    const {addprice} = priceActionCreators;
    addprice(
      {
      levelprice01:formatCash(levelprice01),
      levelprice02:formatCash(levelprice02),
      levelprice03:formatCash(levelprice03),
      solarprice:formatCash(solarprice)
      }
    );
  };
  handleSubmitForm02 = data => {
    const {alarmActionCreators} = this.props;
    const {addListEmailAlarm} = alarmActionCreators;
    addListEmailAlarm(data)
  };
  render() {
    const {
      handleSubmit,
      redirectToReferrer,
      listPrice,
      listEmailAlarm,
    } = this.props;

    const columns = [
      {
        id: 'id',
        label: 'id',
        minWidth: 10,
        align: 'center',
      },
      {
        id: 'name',
        label: 'Tên',
        minWidth: 100,
        align: 'center',
      },
      {
        id: 'email',
        label: 'Email',
        minWidth: 170,
        align: 'center',
      },
      {
        id: 'time',
        label: 'Thời gian',
        minWidth: 170,
        align: 'center',
      },
    ];

    const columns1 = [
      {
        id: 'id',
        label: 'id',
        minWidth: 10,
        align: 'center',
      },
      {
        id: 'levelprice01',
        label: 'price 50kW',
        minWidth: 100,
        align: 'center',
      },
      {
        id: 'levelprice02',
        label: 'price 100kW',
        minWidth: 100,
        align: 'center',
      },
      {
        id: 'levelprice03',
        label: 'price 200kW',
        minWidth: 100,
        align: 'center',
      },
      {
        id: 'solarprice',
        label: 'price Solar',
        minWidth: 100,
        align: 'center',
      },
      {
        id: 'time',
        label: 'time',
        minWidth: 100,
        align: 'center',
      },
    ];
    if (
      redirectToReferrer === false &&
      localStorage.getItem('token:') === null
    ) {
      return <Redirect to={'/'} />;
    }

    return (
      <div className="p-1 pt-5" style={{}}>
        <Grid container spacing={1}>
          <Grid item xs={12} md={12} style={{ textAlign: 'center' }}>
            <label
              className="font-weight-bolder h6 w-100 mb-1 p-1"
              style={{
                height: 'fit-content',
                maxHeight: '40vh',
                borderBottom: '2px solid #00CC00',
              }}
            >
              Cài đặt chung
            </label>
          </Grid>
          <Grid item xs={12} md={12}>
            <form onSubmit={handleSubmit(this.handleSubmitForm01)}>
              <Grid container spacing={1} className=" pl-4 pr-4 pb-2">
                <Grid item xs={12} md={12} className="textAlign: center">
                  <label
                    className="font-weight-bolder h6 w-100 mb-1 p-1"
                    style={{
                      height: 'fit-content',
                      maxHeight: '40vh',
                      borderBottom: '2px solid #00CC00',
                    }}
                  >
                    Cài đặt giá điện:
                  </label>
                </Grid>
                <Grid item sx={12} md={3}>
                  <Field
                    id="price1"
                    label="Giá điện mức 50kWh"
                    name="levelprice01"
                    fullWidth
                    inputProps={{ style: { fontSize: 15 } }} // font size of input text
                    InputLabelProps={{ style: { fontSize: 15 } }} // font size of input label
                    type="text"
                    size="small"
                    component={renderTextField}
                  />
                </Grid>
                <Grid item sx={12} md={3}>
                  <Field
                    id="price2"
                    label="Giá điện mức 100kWh"
                    name="levelprice02"
                    fullWidth
