import React, { Component } from 'react';
import styles from './styles';
import { withStyles} from '@material-ui/core/styles';
import PropTypes from 'prop-types';
import ReactApexChart from "react-apexcharts";
class AreaChart extends Component {
    render() {
        const series = [{
            name:'power',
            data: [["2020-09-23T05:00:00Z",52],["2020-09-23T05:05:00Z",125],["2020-09-23T05:10:00Z",80],["2020-09-23T08:10:00Z",150],["2020-09-24T05:00:00Z",82],["2020-09-24T08:05:00Z",32],["2020-09-24T09:10:00Z",80],["2020-09-24T12:10:00Z",120],["2020-10-02T08:05:00Z",232],["2020-10-02T09:10:00Z",80],["2020-10-02T12:10:00Z",20]]

           }]
          const options = {
            chart: {
              id: 'area-datetime',
              type: 'area',
              zoom: {
                autoScaleYaxis: true
              }
            },
            title: {    
              //text:"Trend Power",
              align: 'left',
              margin: 10,
              offsetX: 0,
              offsetY: 0,
              floating: false,
              style: {
                fontSize:  '16px',
                fontWeight:  'bold',
                fontFamily:  undefined,
                color:  '#263238'
              }
            },
            dataLabels: {
              enabled: false
            },
            markers: {
              size: 0,  
              style: 'hollow',
            },
            xaxis: {
              type: 'datetime',
              min: new Date("2020--02").getTime(),
            },
            yaxis: {
              title: {
                //text: 'power'
              },
              min: 0,
            },
            tooltip: {
              x: {
                format: 'dd MMM yyyy'
              }
            },
            responsive: [
              {
                breakpoint: 1,
                options: {
                  plotOptions: {
                    bar: {
                      horizontal: false
                    }
                  },
                  legend: {
                    position: "bottom"
                  }
                }
              }
            ],
            fill: {
              type: 'gradient',
              gradient: {
                shadeIntensity: 1,
                opacityFrom: 0.7,
                opacityTo: 0.9,
                stops: [0, 100]
              }
            },
          };
        
        return (
            
              <ReactApexChart
                options={options}
                series={series}
                type="area"
                height={290}
                width='100%'
              />
          );
    }
}
AreaChart.propTypes={
    classes:PropTypes.object,
}   
export default withStyles(styles)(AreaChart);
