import React, { Component } from 'react';
import { withStyles, Button} from '@material-ui/core';
import PropTypes from 'prop-types';
import {compose,bindActionCreators} from 'redux';
import {connect} from 'react-redux';
import {Redirect} from 'react-router-dom';
import { Field, reduxForm } from 'redux-form'; 
import Grid from '@material-ui/core/Grid';
import renderTextField from '../../components/FormHelper/TextField/index';
import * as alarmActions from './../../actions/alarm';
import Table from './../../components/Table';
import styles from './styles';


class AlarmPage extends Component {

    componentDidMount(){
        // const { alarmActionCreators }=this.props;
        // const { fetchListAlarm } = alarmActionCreators;
        // fetchListAlarm();
    }
    
    handleSubmitForm = data => {
        //const {devicename,formdate,todate,type}= data;
        // const { alarmActionCreators }=this.props;
        // const { filterAlarm } = alarmActionCreators;
        // filterAlarm(data);
    };
    render() {
        const {handleSubmit,redirectToReferrer}=this.props; 
        const rows = [
            {
            "id" :1,
            "area":"Hồ cá",
            "code":"0x01",
            "time":'2020:11:25 19:15:45', 
            "type":'alarm',
            "discription":"Mất điện khu vực hồ cá",
            "Solution":"Vui lòng kiểm tra aptomat"
            },
            {
                "id" :2,
                "area":"Hồ cá",
                "code":"0x01",
                "time":'2020:11:25 19:15:45', 
                "type":'alarm',
                "discription":"Mất điện khu vực hồ cá",
                "Solution":"Vui lòng kiểm tra aptomat"
                },
                {
                    "id" :3,
                    "area":"Hồ cá",
                    "code":"0x01",
                    "time":'2020:11:25 19:15:45', 
                    "type":'alarm',
                    "discription":"Mất điện khu vực hồ cá",
                    "Solution":"Vui lòng kiểm tra aptomat"
                    },
                    {
                        "id" :4,
                        "area":"Sân thượng",
                        "code":"0x01",
                        "time":'2020:11:25 19:15:45', 
                        "type":'warning',
                        "discription":"Mất điện khu vực hồ cá",
                        "Solution":"Vui lòng kiểm tra aptomat"
                        },
                        {
                            "id" :5,
                            "area":"Hồ cá",
                            "code":"0x01",
                            "time":'2020:11:25 19:15:45', 
                            "type":'alarm',
                            "discription":"Mất điện khu vực hồ cá",
                            "Solution":"Vui lòng kiểm tra aptomat"
                            },
                            {
                                "id" :6,
                                "area":"Hồ cá",
                                "code":"0x01",
                                "time":'2020:11:25 19:15:45', 
                                "type":'warning',
                                "discription":"Mất điện khu vực hồ cá",
                                "Solution":"Vui lòng kiểm tra aptomat"
                                },
                                {
                                    "id" :7,
                                    "area":"Hồ cá",
                                    "code":"0x01",
                                    "time":'2020:11:25 20:20:00', 
                                    "type":'alarm',
                                    "discription":"Mất điện khu vực hồ cá",
                                    "Solution":"Vui lòng kiểm tra aptomat"
                                    }
        ];
        const columns = [
            { id: 'id',
              label: 'id',
              minWidth: 10 ,
              align: 'center',
            },
            { id: 'area',
              label: 'Khu vực',
              minWidth: 100 ,
              align: 'center',
            },
            {
              id: 'code',
              label: 'Code',
              minWidth: 170,
              align: 'center',
            },
            {
              id: 'time',
              label: 'Thời gian',
              minWidth: 170,
              align: 'center',
            },
            {
              id: 'type',
              label: 'Loại',
              minWidth: 170,
              align: 'center',
            },
            {
              id: 'discription',
              label: 'Mô tả',
              minWidth: 170,
              align: 'center',
            },
            {
              id: 'Solution',
              label: 'Giải pháp',
              minWidth: 170,
              align: 'center',
              // format: (value) => value.toFixed(2),
            },
          ];

        if (redirectToReferrer === false &&  localStorage.getItem("token:") === null) {
            return (<Redirect to={'/'}/>)
        }
        
        return(
                <div className="p-3 " style={{}}>
                    <form onSubmit={handleSubmit(this.handleSubmitForm)}>
                        <Grid container spacing={1} className="mr-3 p-4">
                            <Grid item xs={12}  className="textAlign: center" >
                                <label className="font-weight-bolder ">AlarmPage Devices</label>
                            </Grid>
                            <Grid item sx={12} md={3}>
                                <Field
                                    id="devicename"
                                    label="Device Name"
                                    name="devicename"
                                    inputProps={{style: {fontSize: 15}}} // font size of input text
                                    InputLabelProps={{style: {fontSize: 15}}} // font size of input label
                                    type="text"  
                                    size="small"
                                    component={renderTextField}
                                    // value={userEditting ? userEditting.name :''}
                                    />
                            </Grid>
                            <Grid item sx={12} md={3}>
                                <Field
                                    id="fromdate"
                                    label="From Date"
                                    name="fromdate"
                                    inputProps={{style: {fontSize: 15}}} // font size of input text
                                    // InputLabelProps={{}} // font size of input label
                                    type="date"
                                    InputLabelProps={{
                                        shrink: true,
                                    style: {fontSize: 15}
                                    }}
                                    size="small"
                                    component={renderTextField}
                                    // value={userEditting ? userEditting.name :''}
                                    />
                            </Grid>
                            <Grid item sx={12} md={3}>
                                <Field
                                    id="todate"
                                    label="Todate"
                                    name="todate"
                                    inputProps={{style: {fontSize: 15}}} // font size of input text
                                    // InputLabelProps={{style: {fontSize: 12}}} // font size of input label
                                    type="date"
                                    InputLabelProps={{
                                        shrink: true,
                                        style: {fontSize: 15}
                                        }}
                                    size="small"
                                    component={renderTextField}
                                    // value={userEditting ? userEditting.name :''}
                                    />
                            </Grid>
                            <Grid item sx={12} md={3}>
                                <Field
                                    id="type"
                                    label="Type Alarm"
                                    name="type"
                                    inputProps={{style: {fontSize: 15}}} // font size of input text
                                    InputLabelProps={{style: {fontSize: 15}}} // font size of input label
                                    type="text"
                                    size="small"
                                    component={renderTextField}
                                    // value={userEditting ? userEditting.name :''}
                                    />
                            </Grid>
                            <Button
                                className="mt-3"
                                color = "primary"
                                variant="contained"
                                type="submit"
                                size='small'
                                >
                                    Filter
                            </Button>
                        </Grid>
                    </form>
                    <Grid container spacing={1} className="mr-3 p-2 mb-0 max-h-50" style={{backgroundColor:'rgba( 0, 0, 0, 0.2)',border: '2px solid #00BFFF'}}>
                        <label className="font-weight-bolder h6" style={{height:'fit-content',maxHeight:'40vh' }}> Result of Alarm Devices</label>
                        <Table 
                        columns={columns}
                        rows = {rows.filter(row => 
                            row.type
                            .trim()
                            .toLowerCase()
                            .includes(""
                                .toString()
                                .trim()
                                .toLowerCase()
                            )
                            &row.area
                            .trim()
                            .toLowerCase()
                            .includes(""
                                .toString()
                                .trim()
                                .toLowerCase()
                            )&row.time.includes('2020:11:25 20:20:00')
                        )}/>
                    </Grid>
                </div>
        );
    }
}

AlarmPage.propTypes={
    classes:PropTypes.object,
    handleSubmit:PropTypes.func,
    invalid:PropTypes.bool,
    submitting:PropTypes.bool,
    listAlarm:PropTypes.array,
    alarmActionCreators:PropTypes.shape({
        fetchListAlarm:PropTypes.func,
    }),
}

const FORM_ALARM ="TASK_ALARM";
const withReduxForm = reduxForm({
    form: FORM_ALARM,
});
const mapStateToProps=state=>{
    console.log(state.alarm.listAlarm)
    return{
        listAlarm:state.alarm.listAlarm,
        redirectToReferrer:state.auth.redirectToReferrer,
    }
};
const mapDispatchToProps=(dispatch,props)=>{
    return{
        alarmActionCreators:bindActionCreators(alarmActions,dispatch),
    }
};
const withConnect=connect(mapStateToProps,mapDispatchToProps);
export default compose(
    withStyles(styles),
    withConnect,
    withReduxForm,
)(AlarmPage);
