import  AxiosService from '../services/axiosService';
import qs from 'query-string';

const API_ENDPOINT = "http://api.openweathermap.org/data/2.5/weather?q=Hanoi&appid=5b7aced2e33ce6c8cf6a0e7cb1b1aa69&units=metric" 

export const getListWeather=(params)=>{
    console.log(params.pa)
    return AxiosService.get(`http://api.openweathermap.org/data/2.5/weather?q=${params}&appid=5b7aced2e33ce6c8cf6a0e7cb1b1aa69&units=metric`)
}
