import * as weatherConstants from './../constants/weather';

/** refesh weather list api */
export const refeshW = (params={}) => {
    return {
        type: alarmConstants.REFESH_WEATHER,
        payload:{
            params,
        }
    };
};
export const refeshWFailed = error => {
    return {
        type: alarmConstants.REFESH_WEATHER_FAILED,
        payload: {
            error,
        }
    };
};
export const refeshWSuccess = data => {
    return {
        type: alarmConstants.REFESH_WEATHER_SUCCESS,
        payload: {
            data,
        }
    };
};
