const styles =()=>({
    globalLoading:{
        position: 'fixed',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        zIndex: 99,
        backgroundColor: 'rgba(0,0,0,0.4)',
    },
    icon:{
        position: 'fixed',
        left: 0,
        right: 0,
        marginLeft: 'auto',
        marginRight: 'auto',
        top:'40%',
        width: 80
    }
});
export default styles;
